println "Loading properties..."
String projectPath = testRunner.testCase.testSuite.project.path.replaceAll ("\\\\", "/")
String propsPath =  projectPath.substring(0, projectPath.lastIndexOf("/")+1)+"testdata.properties"
def props = new Properties()
new File(propsPath).withInputStream { 
  stream -> props.load(stream) 
}
testRunner.testCase.setPropertyValue("firstOperand", props["bothPositive.firstOperand"])
testRunner.testCase.setPropertyValue("secondOperand", props["bothPositive.secondOperand"])
testRunner.testCase.setPropertyValue("expectedResponse", props["bothPositive.expectedResponse"])

