con:response.con:script:
import groovy.util.XmlSlurper
def parsedContent = new XmlSlurper().parseText(mockRequest.requestContent)
//parsing 'type' attribute from trip tag in the last request
context.tripType=parsedContent.Body.addBusRequest.@tripType.toString()

con:mockOperation.con:dispatchPath:
import groovy.util.XmlSlurper

def parsedContent = new XmlSlurper().parseText(mockRequest.requestContent)

def user = parsedContent.Header.Security.UsernameToken.Username.toString()
def pswd = parsedContent.Header.Security.UsernameToken.Password.toString()

if (user!='bob'||pswd!='bob'){
	context.username=user
	context.password=pswd
	return 'AuthFailure'
}

int tripLength = Integer.parseInt(parsedContent.Body.addTripRequest.timeInWay.toString())
if (tripLength>5){
	context.tripLength=tripLength
	return 'TooLongTripFailure'
}
else if (tripLength==0){
	return "EmptyResponse"
}

